mapper.select:

left join dj_activity_arrange arrange
     on arrange.plan_uuid = detailed.plan_uuid  and arrange.party_org_id = detailed.party_org_id
left join dj_activity_plan plan on arrange.plan_uuid = plan.plan_uuid

     and detailed.detailed_uuid = #{detailedUuid}
     and detailed.plan_uuid = #{planUuid}
     and detailed.party_org_id = #{partyOrgId}
     and detailed.cycle = #{cycle}
     and detailed.party_member_id = #{partyMemberId}
     and detailed.venue = #{venue}

     and detailed.activity_content = #{activityContent}
     and detailed.plan_start_time = #{planStartTime}
     and detailed.plan_end_time = #{planEndTime}
     and detailed.activity_plan_start_time = #{activityPlanStartTime}
     and detailed.activity_plan_end_time = #{activityPlanEndTime}
     and detailed.actual_start_time = #{actualStartTime}
     and detailed.actual_end_time = #{actualEndTime}
     and detailed.recorder = #{recorder}
     and detailed.mentors = #{mentors}
     and detailed.presenter = #{presenter}
     and detailed.speaker = #{speaker}
     and detailed.allow_comment = #{allowComment}
     and detailed.activity_vote_id = #{activityVoteId}
     and detailed.activity_exam_id = #{activityExamId}
     and detailed.status = #{status}
    ${dataScope}

mapper.update:
update dj_activity_detailed
plan_uuid = #{planUuid}, party_org_id = #{partyOrgId}, cycle = #{cycle}, party_member_id = #{partyMemberId}, venue = #{venue}, activity_content = #{activityContent}, plan_start_time = #{planStartTime}, plan_end_time = #{planEndTime}, activity_plan_start_time = #{activityPlanStartTime}, activity_plan_end_time = #{activityPlanEndTime}, actual_start_time = #{actualStartTime}, actual_end_time = #{actualEndTime}, recorder = #{recorder}, mentors = #{mentors}, presenter = #{presenter}, speaker = #{speaker}, allow_comment = #{allowComment}, activity_vote_id = #{activityVoteId}, activity_exam_id = #{activityExamId}, status = #{status}, del_flag = #{delFlag}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime},
where detailed_id = #{detailedId}

update dj_activity_detailed
plan_uuid = #{planUuid}, party_org_id = #{partyOrgId}, cycle = #{cycle}, party_member_id = #{partyMemberId}, venue = #{venue}, activity_content = #{activityContent}, plan_start_time = #{planStartTime}, plan_end_time = #{planEndTime}, activity_plan_start_time = #{activityPlanStartTime}, activity_plan_end_time = #{activityPlanEndTime}, actual_start_time = #{actualStartTime}, actual_end_time = #{actualEndTime}, recorder = #{recorder}, mentors = #{mentors}, presenter = #{presenter}, speaker = #{speaker}, allow_comment = #{allowComment}, activity_vote_id = #{activityVoteId}, activity_exam_id = #{activityExamId}, status = #{status}, del_flag = #{delFlag}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime},
where plan_uuid = #{planUuid} and party_org_id = #{partyOrgId}
