mapper.select:

left join dj_org_assessmentyear doay on doay.assessmentYear_uuid = doa.assessmentYear_uuid
left join dj_party_org dpo on dpo.party_org_id = doa.party_org_id


         and doay.org_assessment_status = #{assessmentyear.orgAssessmentStatus}


        and doay.year = #{assessmentyear.year}


        and doay.assessment_name like  concat('%', #{assessmentyear.assessmentName}, '%')


        and dpo.performance_appraisal = #{djPartyOrg.performanceAppraisal}

     and doa.id = #{id}
     and doa.assessment_uuid = #{assessmentUuid}
     and doa.assessmentYear_uuid = #{assessmentyearUuid}
     and doa.party_org_id = #{partyOrgId}
     and doa.assessment_result = #{assessmentResult}
     and doa.org_assessment_status = #{orgAssessmentStatus}
     and doa.assessment_score = #{assessmentScore}

        and doa.assessment_self_score = #{assessmentSelfScore}

       and doa.performance_appraisal_status = #{performanceAppraisalStatus}


        and doa.add_subtract_score = #{addSubtractScore}

       and doa.performance_appraisal_score = #{performanceAppraisalScore}

        and doa.score = #{score}
     and doa.assessment_rank = #{assessment_rank}
    ${dataScope}

order by  doay.year desc

mapper.update:
update dj_org_assessment
assessment_uuid = #{assessmentUuid}, assessmentYear_uuid = #{assessmentyearUuid}, party_org_id = #{partyOrgId}, assessment_result = #{assessmentResult}, org_assessment_status = #{orgAssessmentStatus}, assessment_rank = #{assessment_rank}, assessment_self_score = #{assessmentSelfScore}, assessment_score = #{assessmentScore}, assessment_score_ratio = #{assessmentScoreRatio}, performance_appraisal_status = #{performanceAppraisalStatus}, add_subtract_score = #{addSubtractScore}, performance_appraisal_score = #{performanceAppraisalScore}, performance_appraisal_score_ratio = #{performanceAppraisalScoreRatio}, score = #{score}, del_flag = #{delFlag}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime},
where id = #{id}
